CREATE PROCEDURE [job].[MachineUpdate]
@Version				timestamp,
	@Id int,
	@Machinename varchar(50),
	@Machinetypeid int,
	@Machinelocationid int
    
AS

SET NOCOUNT ON

UPDATE [job].[Machines] SET
	[MachineName] = @Machinename,
	[MachineTypeId] = @Machinetypeid,
	[MachineLocationId] = @Machinelocationid
    OUTPUT inserted.Id AS [ID], inserted.[RowVersion] as [VERSION]
WHERE
	[Id] = @Id
AND [RowVersion] = @Version	

