CREATE PROCEDURE [messaging].[InstantMessagesGetLatestBySenderUserIdAndRecipientUserId]
	@SenderId INT,
  @RecipientId INT
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[Id],
	[Created],
	[CreatedById],
  IsRead,
	[Message],
	[RecipientUserId],
	[SenderUserId],
	[RowVersion]
FROM
	[messaging].[InstantMessages]
WHERE
	([RecipientUserId] = @RecipientId AND SenderUserId = @SenderId) OR ([RecipientUserId] = @SenderId AND SenderUserId = @RecipientId)
  ORDER BY Created 
--endregion
GO